import React from "react";
import * as tf from '@tensorflow/tfjs';
import * as mobilenet from '@tensorflow-models/mobilenet';
import * as jpeg from 'jpeg-js'
import studio4 from "../../assets/img/olu-eletu.jpg";

//import Constants from 'expo-constants'
//import * as Permissions from 'expo-permissions'
//import '@tensorflow/tfjs-react-native';
 
export default class TheMl extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isTfReady: false,
      isModelReady: false,
      predictions: null,
      image: null
    };
  }
 
  async componentDidMount() {
    // Wait for tf to be ready.
    await tf.ready();
    // Signal to the app that tensorflow.js can now be used.
    this.setState({
      isTfReady: true,
    })
    this.model = await mobilenet.load()
    this.setState({ isModelReady: true })

    //const source = { uri: response.uri }
    this.setState({ image: {studio4} })
    this.classifyImage();

  }

  imageToTensor(rawImageData) {
    const TO_UINT8ARRAY = true
    const { width, height, data } = jpeg.decode(rawImageData, TO_UINT8ARRAY)
    // Drop the alpha channel info for mobilenet
    const buffer = new Uint8Array(width * height * 3)
    let offset = 0 // offset into original data
    for (let i = 0; i < buffer.length; i += 3) {
      buffer[i] = data[offset]
      buffer[i + 1] = data[offset + 1]
      buffer[i + 2] = data[offset + 2]

      offset += 4
    }

    return tf.tensor3d(buffer, [height, width, 3])
  }

  classifyImage = async () => {
    try {
      //const imageAssetPath = Image.resolveAssetSource(this.state.image)
      //console.log("1")
      const response = await fetch(this.state.image, {}, { isBinary: true })
      console.log("2")
      const rawImageData = await response.arrayBuffer()
      console.log("3")
      const imageTensor = this.imageToTensor(rawImageData)
      console.log("4")
      const predictions = await this.model.classify(imageTensor)
      this.setState({ predictions })
      console.log(predictions)
    } catch (error) {
      console.log(error)
    }
  }
 
 
  render() {

    const { isTfReady, isModelReady, predictions, image } = this.state
    //
    return (

        <div>
            {this.state.isTfReady?<p>TF Is Ready</p>:<p>yes</p>}
            <p>
                Model ready?{' '}
                {this.state.isModelReady ? <p>Model has been Loaded</p> : <p>Loading Model...</p>}

                {this.state.image ? <p>Image loaded</p> : <p>Waiting for Image...</p>}
                {this.state.predictions ? <p>Predictions Loaded</p> : <p>Loading Predictions...</p>}
                
            </p>
        </div>

    );
  }
}
