import React from 'react'
// nodejs library to set properties for components
import PropTypes from "prop-types";
// nodejs library that concatenates classes
import classNames from "classnames";
import Grid from '@material-ui/core/Grid'
import Box from '@material-ui/core/Box'
import {makeStyles} from '@material-ui/core/styles'
import ButtonGroup from '@material-ui/core/ButtonGroup'
import Slider from '@material-ui/core/Slider'
import Switch from '@material-ui/core/Switch'
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup'
import Toggle from '@material-ui/lab/ToggleButton'
import Skeleton from '@material-ui/lab/Skeleton'
import TextField from '@material-ui/core/TextField'
import useMediaQuery from '@material-ui/core/useMediaQuery'
import Popover from '@material-ui/core/Popover'
import Popper from '@material-ui/core/Popper'
import Paper from '@material-ui/core/Paper'
import MobileStepper from '@material-ui/core/MobileStepper'
import LinearProgress from '@material-ui/core/LinearProgress'
import InputLabel from '@material-ui/core/InputLabel'
import Input from '@material-ui/core/InputBase'
import FilledInput from '@material-ui/core/FilledInput'
import Divider from '@material-ui/core/Divider'
import Checkbox from '@material-ui/core/Checkbox'
import styles from "../../assets/jss/material-kit-react/components/customInputStyle.js"

const anotherStyle = makeStyles(styles)
const useStyles = makeStyles(theme => ({
    root: {
      marginTop:theme.spacing(10),
      marginLeft:theme.spacing(10),
      marginRight:theme.spacing(10),
      marginBottom:theme.spacing(10)
    },
    grid1: {
        marginTop:theme.spacing(10),
        marginLeft:theme.spacing(10),
        marginRight:theme.spacing(10),
        marginBottom:theme.spacing(10)
      },
      grid2: {
        marginTop:theme.spacing(10)
      }
  }));




export default function Play(){
const classes = useStyles();
        return (
            <div>
                <Box >
                    <Grid container>
                       <Grid item xs={3} className={classes.root}>
                           <Slider/>
                           <Slider/>
                           <Slider/>
                           <Slider/>
                        </Grid>
                        <Grid item xs={3} className={classes.root}>
                            <h4>Input Optimizer</h4>
                            <Input/>
                            <h4>Input Loss</h4>
                            <Input/>
                            <h4>Input Metrics</h4>
                            <Input/>
                            <h5>Optional Choose network Type, LSTM, Etc</h5>
                        </Grid>
                        <Grid item xs={3} className={classes.grid2}>
                            <h4>Units Input</h4>
                            <Input/>
                            <h4>Multiple Layers?</h4>
                            <Slider/>
                            <h4>Input Shape</h4>
                            <Input/>
                            <ToggleButtonGroup>
                                <Toggle/>
                                <Toggle/>
                                <Toggle/>
                                <Toggle/>
                                <Toggle/>
                            </ToggleButtonGroup>
                        </Grid>
                    </Grid>

                    <Checkbox/>
                    <CustomInput/>
                </Box>
            </div>
        );
}
function CustomInput(props) {
    const classes = anotherStyle();
    const {
      formControlProps,
      labelText,
      id,
      labelProps,
      inputProps,
      error,
      white,
      inputRootCustomClasses,
      success
    } = props;
  
    const labelClasses = classNames({
      [" " + classes.labelRootError]: error,
      [" " + classes.labelRootSuccess]: success && !error
    });
    const underlineClasses = classNames({
      [classes.underlineError]: error,
      [classes.underlineSuccess]: success && !error,
      [classes.underline]: true,
      [classes.whiteUnderline]: white
    });
    const marginTop = classNames({
      [inputRootCustomClasses]: inputRootCustomClasses !== undefined
    });
    const inputClasses = classNames({
      [classes.input]: true,
      [classes.whiteInput]: white
    });
    var formControlClasses;
    if (formControlProps !== undefined) {
      formControlClasses = classNames(
        formControlProps.className,
        classes.formControl
      );
    } else {
      formControlClasses = classes.formControl;
    }
    return (
      <FormControl {...formControlProps} className={formControlClasses}>
        {labelText !== undefined ? (
          <InputLabel
            className={classes.labelRoot + " " + labelClasses}
            htmlFor={id}
            {...labelProps}
          >
            {labelText}
          </InputLabel>
        ) : null}
        <Input
          classes={{
            input: inputClasses,
            root: marginTop,
            disabled: classes.disabled,
            underline: underlineClasses
          }}
          id={id}
          {...inputProps}
        />
      </FormControl>
    );
  }
  
  CustomInput.propTypes = {
    labelText: PropTypes.node,
    labelProps: PropTypes.object,
    id: PropTypes.string,
    inputProps: PropTypes.object,
    formControlProps: PropTypes.object,
    inputRootCustomClasses: PropTypes.string,
    error: PropTypes.bool,
    success: PropTypes.bool,
    white: PropTypes.bool
  };
