import React from "react";
//import * as t_f from '@tensorflow/tfjs-node'
import * as tf from '@tensorflow/tfjs';

//const model = tf.sequential();


const model = tf.sequential({
    layers: [
      tf.layers.dense({inputShape: [784], units: 32, activation: 'relu'}),
      tf.layers.dense({units: 10, activation: 'softmax'}),
    ]
   });

export default class AnotherMl extends React.Component{

    constructor(props) {

        super(props);
        model.compile({
            optimizer: 'sgd',
            loss: 'categoricalCrossentropy',
            metrics: ['accuracy']
          });

        this.state = {
            isTfReady:false,
            trained:false,
            isitraining: false,
            prediction:null
        }
        
      }
    
    async componentDidMount() {
        // Wait for tf to be ready.
        await tf.ready();
        // Signal to the app that tensorflow.js can now be used.
        this.setState({isTfReady: true})
        //this.train()
        //this.setState({training:true})
        //const source = { uri: response.uri }
        this.trainTheModel()

    }
    

    async trainTheModel(){


        // Generate dummy data.
        const data = tf.randomNormal([100, 784]);
        const labels = tf.randomUniform([100, 10]);

        function onBatchEnd(batch, logs) {
            console.log('Accuracy', logs.acc);
            //)
        }

        function isTraining(){
            this.setState({isitraining:true})
        }

        // Train for 5 epochs with batch size of 32.
        model.fit(data, labels, {
        epochs: 5,
        batchSize: 32,
        callbacks: {onBatchEnd, isTraining}
        }).then(info => {
            //this.setState({isitraining:false})
            console.log('Final accuracy', info.history.acc);
            this.setState({trained:true})
        });

            //this.thePrediction()
        
    }

    async thePrediction(){
        const prediction_ = model.predict(tf.randomNormal([3, 784]));
        console.log(prediction_?"yes":"no")
        this.setState({prediction: prediction_})
    }


    render()
        {
            const { isTfReady, isitraining, trained, prediction } = this.state

            return (

            <div>

                {this.state.isTfReady?<p>Yes Ready</p>:<p>Not Ready</p>}
                {this.state.isitraining?<p>Yes Training</p>:<p>Not Training</p>}
                {this.state.trained?<p>Yes Trained</p>:<p>Not Trained</p>}
                {this.state.prediction? JSON.stringify(this.state.prediction): <p>Not</p>}
                
                
                
            </div>
            );
        }
    

}
