import React, { Component } from 'react'
import Chart from "chart.js";
import { makeStyles } from '@material-ui/core/styles';
import classes from "../../assets/css/LineGraph.module.css";
import Grid from '@material-ui/core/Grid';




export default class LineGraph extends Component {


    useStyles = makeStyles(theme => ({
        root: {
          position: 'center'
        },
        theintro: {
          background: 'linear-gradient(45deg, #F8B8B 40%, #AA8EFF 90%)',
          marginTop: theme.spacing(10),
          marginBottom: theme.spacing(10)
        },
        grid1: {
          background: 'linear-gradient(45deg, #B6CF 40%, #AA8EFF 90%)',
          marginLeft: theme.spacing(5)
        },
        grid2: {
          background: 'linear-gradient(45deg, #B6CF 40%, #AA8EFF 90%)',
          marginLeft: theme.spacing(15)
        },
      
        grid3: {
          background: 'linear-gradient(45deg, #BF8B 40%, #AA8EFF 90%)',
          marginLeft: theme.spacing(17)
        }
      }));



    classes = useStyles();
    chartRef = React.createRef();
    
    componentDidMount() {
        const myChartRef = this.chartRef.current.getContext("2d");
        
        new Chart(myChartRef, {
            type: "line",
            data: {
                //Bring in data
                labels: ["Jan", "Feb", "March"],
                datasets: [
                    {
                        label: "Sales",
                        data: [86, 67, 91],
                    }
                ]
            },
            options: {
                //Customize chart options
                width : '225px',
                height: '225px'
            }
        });
    }
    render() {
        return (
            <div >
                <Grid container spacing={10}>
                    <Grid item xs={4} >
                        <canvas
                            id="myChart"
                            ref={this.chartRef}
                        />
                    </Grid>
                    <Grid item xs={4}>
                        <canvas
                            id="myChart"
                            ref={this.chartRef}
                        />
                    </Grid>
                </Grid>
            </div>
        )
    }
}
